print("Guess a number between 1 to 100 and follow my instructions...")input()print("Multiply it by 2")input()print("Add 2")input()print("Multiply it by 5")input()print("Add 5")input()print("Multiply it by 10")input()print("Add 10")input()print("\nEnter the final answer and I'll try to guess it...")num = int(input())newNum = str(num)res = [int(x) for x in newNum]print("\nOkay lemme guess your number. Press Enter")input()if len(res) == 3:    n = str(res[0])    n = int(n)    n = n - 1elif len(res) == 4:    n = str(str(res[0]) + str(res[1]))    n = int(n)    n = n - 1elif len(res) == 5:    n = str(str(res[0]) + str(res[1]) + str(res[2]))    n = int(n)    n = n - 1if n > 100:    print("Guessed number is greater than 100")else:    print("Guessed number is : ", n)