def triplet(arr, n, sum):    count = 0    arr.sort()    for i in range(0, len(arr) - 2):        s = i + 1        e = n - 1        while s < e:            if (arr[i] + arr[s] + arr[e]) == sum:                count += 1                s = s + 1                e = e - 1            elif (arr[i] + arr[s] + arr[e]) < sum:                s = s + 1            elif (arr[i] + arr[s] + arr[e]) > sum:                e = e - 1    return countarr = [2, 5, 7, 9, 12, 6, 4]# 2, 4, 5, 6, 7, 9, 12print(triplet(arr, 7, 17))